import React, { Component, Fragment } from "react";
import ReactDice from "react-dice-complete";
import "react-dice-complete/dist/react-dice-complete.css";
import random from "random";
class Dice extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sum: 0,
      op: ["", "", ""],
    };
    this.rollDoneCallback = this.rollDoneCallback.bind(this);
  }
  rollAll() {
    this.reactDice.rollAll();
  }

  rollDoneCallback(num) {
    const nop = [random.int(3, 36), random.int(3, 36), random.int(3, 36)];
    console.log(`You rolled a ${num}`);
    this.setState({ sum: num });
    var ix = random.int(0, 2);
    nop[ix] = num;
    this.setState({ op: nop });
  }

  render() {
    return (
      <Fragment>
        <button className="roll" onClick={() => this.rollAll()}>
          Roll Dice
        </button>
        <ReactDice
          numDice={this.props.number}
          faceColor="grey"
          dotColor="rgb(0,0,0)"
          dieSize="50"
          rollTime=".1"
          rollDone={this.rollDoneCallback}
          disableIndividual="true"
          ref={(dice) => (this.reactDice = dice)}
        />
        {this.state.op.map((key) => {
          return <button className="roll" value={key}></button>;
        })}
        {/* <button className="roll" value={this.state.op.map()}></button>
        <button className="roll" value={this.state.op2}></button>
        <button className="roll" value={this.state.op1}></button> */}
        <h1>{this.state.sum}</h1>
      </Fragment>
    );
  }
}

export default Dice;
